@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Forest Green Nature Theme */
    --background: 145 30% 97%;
    --foreground: 150 30% 15%;

    --card: 145 25% 98%;
    --card-foreground: 150 30% 15%;

    --popover: 145 25% 98%;
    --popover-foreground: 150 30% 15%;

    --primary: 152 55% 40%;
    --primary-foreground: 0 0% 100%;

    --secondary: 200 60% 50%;
    --secondary-foreground: 0 0% 100%;

    --accent: 30 90% 55%;
    --accent-foreground: 0 0% 100%;

    --muted: 145 20% 92%;
    --muted-foreground: 150 15% 45%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;

    --border: 145 20% 88%;
    --input: 145 20% 88%;
    --ring: 152 55% 40%;

    --radius: 1rem;

    /* Custom Gradients */
    --gradient-nature: linear-gradient(135deg, hsl(152 55% 40%) 0%, hsl(175 50% 45%) 100%);
    --gradient-hero: linear-gradient(180deg, hsl(152 55% 40% / 0.1) 0%, hsl(145 30% 97%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(145 25% 100%) 0%, hsl(145 25% 98%) 100%);
    --gradient-sunset: linear-gradient(135deg, hsl(30 90% 55%) 0%, hsl(350 75% 55%) 100%);

    /* Shadows */
    --shadow-nature: 0 10px 40px -10px hsl(152 55% 40% / 0.2);
    --shadow-card: 0 4px 20px -4px hsl(150 30% 15% / 0.08);
    --shadow-glow: 0 0 30px hsl(152 55% 40% / 0.3);

    --sidebar-background: 145 30% 97%;
    --sidebar-foreground: 150 30% 15%;
    --sidebar-primary: 152 55% 40%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 145 25% 94%;
    --sidebar-accent-foreground: 150 30% 15%;
    --sidebar-border: 145 20% 88%;
    --sidebar-ring: 152 55% 40%;
  }
}

@layer utilities {
  .text-gradient {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-nature);
  }

  .bg-nature {
    background-image: var(--gradient-nature);
  }

  .bg-hero {
    background-image: var(--gradient-hero);
  }

  .shadow-nature {
    box-shadow: var(--shadow-nature);
  }

  .shadow-card {
    box-shadow: var(--shadow-card);
  }

  .glass {
    @apply bg-card/80 backdrop-blur-xl border border-border/50;
  }

  .card-hover {
    @apply transition-all duration-300 hover:shadow-nature hover:-translate-y-1;
  }
}

@layer components {
  .nav-item {
    @apply flex flex-col items-center gap-1 px-3 py-2 text-muted-foreground transition-colors;
  }

  .nav-item-active {
    @apply text-primary;
  }

  .badge-difficulty {
    @apply px-2 py-0.5 rounded-full text-xs font-medium;
  }

  .badge-easy {
    @apply bg-primary/10 text-primary;
  }

  .badge-medium {
    @apply bg-accent/10 text-accent;
  }

  .badge-hard {
    @apply bg-destructive/10 text-destructive;
  }
}

