import { Map, Users, CalendarDays, MessageCircle, Home } from "lucide-react";
import { useLocation, Link } from "react-router-dom";

const navItems = [
  { icon: Home, label: "首页", path: "/" },
  { icon: Map, label: "地图", path: "/map" },
  { icon: Users, label: "组队", path: "/teams" },
  { icon: CalendarDays, label: "计划", path: "/plans" },
  { icon: MessageCircle, label: "社区", path: "/community" },
];

export default function BottomNav() {
  const location = useLocation();

  return (
    <nav className="fixed bottom-0 left-0 right-0 glass border-t border-border/50 pb-safe z-50">
      <div className="flex justify-around items-center py-2">
        {navItems.map((item) => {
          const isActive = location.pathname === item.path;
          return (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${isActive ? "nav-item-active" : ""}`}
            >
              <item.icon className={`w-5 h-5 ${isActive ? "animate-float" : ""}`} />
              <span className="text-xs font-medium">{item.label}</span>
            </Link>
          );
        })}
      </div>
    </nav>
  );
}

import { MapPin, Clock, TrendingUp } from "lucide-react";

interface TrailCardProps {
  image: string;
  name: string;
  location: string;
  distance: string;
  duration: string;
  difficulty: "easy" | "medium" | "hard";
  elevation: string;
}

const difficultyMap = {
  easy: { label: "简单", class: "badge-easy" },
  medium: { label: "中等", class: "badge-medium" },
  hard: { label: "困难", class: "badge-hard" },
};

export default function TrailCard({
  image,
  name,
  location,
  distance,
  duration,
  difficulty,
  elevation,
}: TrailCardProps) {
  return (
    <div className="bg-card rounded-xl overflow-hidden shadow-card card-hover">
      <div className="relative h-36">
        <img src={image} alt={name} className="w-full h-full object-cover" />
        <span className={`badge-difficulty ${difficultyMap[difficulty].class} absolute top-3 right-3`}>
          {difficultyMap[difficulty].label}
        </span>
      </div>
      <div className="p-4">
        <h3 className="font-semibold text-foreground mb-1">{name}</h3>
        <div className="flex items-center gap-1 text-muted-foreground text-sm mb-3">
          <MapPin className="w-3.5 h-3.5" />
          <span>{location}</span>
        </div>
        <div className="flex items-center gap-4 text-sm text-muted-foreground">
          <span className="flex items-center gap-1">
            <span className="font-medium text-foreground">{distance}</span> km
          </span>
          <span className="flex items-center gap-1">
            <Clock className="w-3.5 h-3.5" />
            {duration}
          </span>
          <span className="flex items-center gap-1">
            <TrendingUp className="w-3.5 h-3.5" />
            {elevation}m
          </span>
        </div>
      </div>
    </div>
  );
}

import { Heart, MessageCircle, Share2 } from "lucide-react";
import { useState } from "react";

interface PostCardProps {
  avatar: string;
  userName: string;
  timeAgo: string;
  content: string;
  images: string[];
  likes: number;
  comments: number;
}

export default function PostCard({
  avatar,
  userName,
  timeAgo,
  content,
  images,
  likes,
  comments,
}: PostCardProps) {
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(likes);

  const handleLike = () => {
    setLiked(!liked);
    setLikeCount(liked ? likeCount - 1 : likeCount + 1);
  };

  return (
    <div className="bg-card rounded-xl p-4 shadow-card">
      <div className="flex items-center gap-3 mb-3">
        <img src={avatar} alt={userName} className="w-10 h-10 rounded-full object-cover" />
        <div>
          <h4 className="font-medium text-foreground">{userName}</h4>
          <span className="text-xs text-muted-foreground">{timeAgo}</span>
        </div>
      </div>
      <p className="text-foreground text-sm mb-3 leading-relaxed">{content}</p>
      {images.length > 0 && (
        <div className={`grid gap-2 mb-4 ${images.length === 1 ? "grid-cols-1" : images.length === 2 ? "grid-cols-2" : "grid-cols-3"}`}>
          {images.map((img, idx) => (
            <img
              key={idx}
              src={img}
              alt=""
              className={`rounded-lg object-cover w-full ${images.length === 1 ? "h-48" : "h-28"}`}
            />
          ))}
        </div>
      )}
      <div className="flex items-center gap-6 pt-3 border-t border-border">
        <button
          onClick={handleLike}
          className={`flex items-center gap-1.5 text-sm transition-colors ${
            liked ? "text-destructive" : "text-muted-foreground hover:text-destructive"
          }`}
        >
          <Heart className={`w-4 h-4 ${liked ? "fill-current" : ""}`} />
          <span>{likeCount}</span>
        </button>
        <button className="flex items-center gap-1.5 text-sm text-muted-foreground hover:text-primary transition-colors">
          <MessageCircle className="w-4 h-4" />
          <span>{comments}</span>
        </button>
        <button className="flex items-center gap-1.5 text-sm text-muted-foreground hover:text-primary transition-colors ml-auto">
          <Share2 className="w-4 h-4" />
          <span>分享</span>
        </button>
      </div>
    </div>
  );
}

import { MapPin, Calendar, Clock, MoreVertical } from "lucide-react";

interface PlanCardProps {
  trailName: string;
  location: string;
  date: string;
  time: string;
  status: "upcoming" | "completed" | "draft";
